mapper.select:
select u_id
from acc_pub
where u_nick=#{u_nick}

mapper.update:
		update acc_pri 
		set u_phnum=#{u_phnum}, u_email=#{u_email} 
		where u_id=#{u_id}

update acc_pub
set u_nick=#{u_nick}, u_intro=#{u_intro}
where u_id=#{u_id}

update acc_pri 
set u_pw=#{new_pw}
where u_id=#{u_id}

mapper.insert:
{
call findpw(
#{u_id},
#{u_name},
#{u_email},
#{u_pw, mode=OUT, jdbcType=VARCHAR},
#{find_hint, mode=OUT, jdbcType=VARCHAR}
)
}

mapper.select:
{
call login(
	#{pw }
, 	#{u_id}
,	#{u_nick, mode=OUT, jdbcType=VARCHAR}
,	#{u_session, mode=OUT, jdbcType=VARCHAR}
,	#{u_level, mode=OUT, jdbcType=NUMERIC}
)}
